$(function(){load_cookie();$("#start-num").blur(function(){set_num("start-num","start_num","")});$("#start-num-red").click(function(){set_num("start-num","start_num","red")});$("#start-num-add").click(function(){set_num("start-num","start_num","add")});$("#profit-num").blur(function(){set_num("profit-num","profit_num","")});$("#profit-num-red").click(function(){set_num("profit-num","profit_num","red")});$("#profit-num-add").click(function(){set_num("profit-num","profit_num","add")});$("#now-balance").blur(function(){set_num("now-balance","now_balance","")});$("#now-balance-red").click(function(){set_num("now-balance","now_balance","red")});$("#now-balance-add").click(function(){set_num("now-balance","now_balance","add")});$("#submit").click(function(){show_result()});$("#result-4").click(function(){$("#now-balance").val($(this).text())})});function show_result(){var b=$("#start-num").val();var d=$("#profit-num").val();var c=$("#now-balance").val();var a=count(parseInt(b),parseInt(d),parseInt(c),1)}function count(c,f,d,a){var e=d-a;var b=e+a*2*2;if((b-f)>=c){$("#result-1").text(a);$("#result-2").text(a*2);$("#result-3").text(b-c);$("#result-4").text(e)}else{count(c,f,d,++a)}}function set_num(d,a,c){var b=$("#"+d).val();if(c=="add"){b++}else{if(c=="red"){b--}}$("#"+d).val(b);set_cookie(a,b,90,"/")}function load_cookie(){var a=get_cookie("start_num");if(a!=""){$("#start-num").val(a)}var c=get_cookie("profit_num");if(c!=""){$("#profit-num").val(c)}var b=get_cookie("now_balance");if(b!=""){$("#now-balance").val(b)}}function set_cookie(b,c,a,e){var d=new Date();d.setDate(d.getDate()+a);document.cookie=b+"="+escape(c)+((a==null)?"":";expires="+d.toGMTString())+((e==null)?"":";path="+e)}function get_cookie(b){if(document.cookie.length>0){var c=document.cookie.indexOf(b+"=");if(c!=-1){c=c+b.length+1;var a=document.cookie.indexOf(";",c);if(a==-1){a=document.cookie.length}return unescape(document.cookie.substring(c,a))}}return""};